// Декларації типів для імпорту різних типів файлів у TypeScript

// CSS модулі
declare module '*.css' {
    const content: string;
    export default content;
}

// Зображення
declare module '*.png' {
    const value: string;
    export default value;
}

declare module '*.jpg' {
    const value: string;
    export default value;
}

declare module '*.jpeg' {
    const value: string;
    export default value;
}

declare module '*.svg' {
    const value: string;
    export default value;
}

declare module '*.gif' {
    const value: string;
    export default value;
}

// Шрифти
declare module '*.woff' {
    const value: string;
    export default value;
}

declare module '*.woff2' {
    const value: string;
    export default value;
}

declare module '*.ttf' {
    const value: string;
    export default value;
}

declare module '*.otf' {
    const value: string;
    export default value;
}

declare module '*.eot' {
    const value: string;
    export default value;
}

